import { buscar, buscarPorNombre, buscarNombrePorId, dataBase, obtenerGeneros, gameByGenre, gameByRating, alphabeticalOrder } from './actions'

const initialStore = {
    fullGames: [],
    videoGames: [],
    name: [],
    id: [],
    database: [],
    genres: [],
    platforms: [],
    gamesFilterByGenre: [],
    gamesFilterByRating: [],
    order: [],
}

const reducer = (state=initialStore, action) => {
    switch (action.type){

        case buscar: return {
            ...state,
            videoGames: action.payload/* .filter((e, i) => {
                if(i <= 15){
                    return e
                }
            }), */,
            fullGames: action.payload
        }

        case buscarPorNombre: return {
            ...state,
            name: action.payload
        }

        case buscarNombrePorId: return {
            ...state,
            id: [action.payload]
        }

        case dataBase: return {
            ...state,
            database: action.payload
        }

        case obtenerGeneros: return {
            ...state,
            genres: action.payload
        }


         case gameByGenre: return {
             ...state,
             gamesFilterByGenre: action.payload.filter((e) => {

                 for(let i = 0; i < e.genres.length; i++){
                     if(e.genres[i] === action.name){
                         return e
                     }
                 }
             })
         }

         case gameByRating: return {
             ...state,
             gamesFilterByRating: action.descAsc === 'asc' ? 
             action.payload.sort(function(a, b) {
                if (a.rating > b.rating) {
                  return 1;
                }
                if (a.rating < b.rating) {
                  return -1;
                }
                return 0;
              })
             : action.payload.sort(function(a, b) {
                if (a.rating < b.rating) {
                  return 1;
                }
                if (a.rating > b.rating) {
                  return -1;
                }
                return 0;
              })
         }

             case alphabeticalOrder: return {
             ...state,
             order: action.descAsc === 'Z - A' ?
             action.payload.sort((a, b)=>{
                 if(a.name > b.name) {
                     return -1;
                    }
                  if (a.name < b.name){
                      return 1;
                  } 
                  return 0
             })
             : action.payload.sort((a, b)=>{
                 if(a.name < b.name) return -1;
                 else if (a.name > b.name) 1;
                 else return
             })
         } 

        default: return state;
    }
}

export default reducer
