import Order from "../Order/order"
import s from "./home.module.css"
import InputSearch from './InputSearch'
import Paginado from '../Paginado/paginado';
import Card from '../Card/card';
import style from '../Home/card.module.css'
import loading from '../../assets/loading.gif'

import React, {useEffect,useState} from 'react';

import {connect} from 'react-redux'
import {searchGames,gameById} from '../../redux/actions';

import {NavLink} from 'react-router-dom';





 export function Home (searchGames, videogames, gameById){

    const allVideoGames = videogames

    const [currentPage,setCurrentPage] = useState(1)
    const [gamesPerPage, setGamesPerPage] = useState(15) 
    const indexOfLastGame = currentPage * gamesPerPage  //15
    const indexOfFirstGame = indexOfLastGame - gamesPerPage
    videogames = videogames.slice(indexOfFirstGame,indexOfLastGame)

    const paginado = (pageNumber) =>{
        setCurrentPage(pageNumber)
    }

    useEffect(() => {
        searchGames()
    },[])

return (
    <div className={s.container}>
        <div className={s.orderContainer}>
          <Order />
        </div>
        <div className={s.inputSearchContainer}>
          <InputSearch /> 
        </div>
        { 
            videogames.length? videogames.map(e=> <div className={style.card} onClick={()=>gameById(e.id)}>

               <div className={style.container}>  
               <div >
               <img className={style.image} src={e.img} alt='Imagen de videogames'/>
               </div>    
               <div className = "s.cardDetails">
                    <p className={style.cardReleased}> <strong>{e.releaseDate}</strong></p>  
                    <p className={style.cardTitle}>{e.name}</p>
                    <p className={style.cardDetails}>{e.genres.map((e,i)=>{if(i < 3)return <span className={s.span} >{e.name}</span>})}</p>   
                    <p className={style.cardRating}> Rating: {e.rating}</p>
                    </div>
                    <NavLink className={style.NavLink} to={`/app/${e.id}`}> 
                    <div><p className={style.cardMore}><strong>Find out more</strong></p></div>
                    </NavLink>
                    </div>    
               </div>) : <div className={style.contLoading}>
                <img src={loading} alt='loading gif' className={style.loading}/>
                </div>

            } 

        <Paginado 
                gamesPerPage = {gamesPerPage}
                allVideoGames = {allVideoGames.length}
                paginado = {paginado}
                currentPage={currentPage}

            />

    </div>
)
}

const mapStateToProps = (store) => {
    return{
        videogames: store.videoGames,
        name: store.name,
    }
} 

export default connect(mapStateToProps, {searchGames, gameById})(Home);
