import React, {useEffect} from 'react';
import {connect} from 'react-redux'
import {searchGames} from '../../redux/actions.js';
import s from './card.module.css'


function Card({searchGames, games, nameGame}){
    useEffect(() => {
        searchGames()
    },[])

    function game(){
        if(nameGame.length > 0) return nameGame;
        else return games
    }


    return(
        <div className={s.cards}>
            { 
           game().map(e=> <div className={s.card}>
               <p className={s.title}>{e.name}</p>
               <p className={s.ctnGenresCard}>{e.genres}</p>
               <img className={s.image} src={e.image} alt='Imagen de videogames'/>
               </div>)
            } 
        </div>
    )
}



const mapStateToProps = (store) => {
    return{
        videogames: store.games,
        name: store.nameGame,
    }
} 

export default connect(mapStateToProps, {searchGames})(Card);
