import axios from 'axios';

export function getVideoGames(){
    return async function(dispatch){
        const game = await axios.get('http://localhost:3001/videogames',{

        });
        return dispatch({
            type: 'GET_VIDEOGAMES',
            payload: game.data
        })
    }
}
