import React from 'react';
import { useState, useEffect} from 'react';
import { useDispatch, useSelector} from 'react-redux';
import { getVideoGames } from '../../actions/index.js';
import { Link } from 'react-router-dom';
import { Card } from './card.jsx';

export default function Home (){
    const dispatch = useDispatch();
    const videoGamesAll = useSelector((state) => state.videogames)

    useEffect(() => {
        dispatch(getVideoGames())
    }, [])

    function handleClick(evt){
        evt.preventDefault();
        dispatch(getVideoGames());
    }

    return(
        <div>
            <Link to = '/videogames/'> Crear Video Games </Link>
            <h1>Quiero ver algo por favor</h1>
            <button onClick={evt => handleClick(evt)}>refresh</button>

            <div>
                <select>
                    <option value = 'asc'>Asc</option>
                    <option value = 'desc'>Desc</option>
                </select>
                {videoGamesAll?.map((games, index) => {
                return(    
                    <div>
                        <Card key={index} name={games.name} image={games.image}/>
                    </div>
                    )
                })}
            </div>
        </div>
    )
}
