const inicialState = {
    videogames : []
}

const rootReducer = (state = inicialState, action) =>{
    switch(action.type){
        case 'GET_VIDEOGAMES':
            return{
                ...state,
                videogames: action.payload,
            }   

            default: return state
         }
}

export default rootReducer;
