import React, {useEffect} from 'react';
import {connect} from 'react-redux'
import {searchGames} from '../../redux/actions.js';


function Card({searchGames, videogames}){
    useEffect(() => {
        searchGames()
    },[])
    return(
        <div>
            { 
           videogames.map(e=> <div>
               <p>{e.name}</p>
               <p>{e.genres}</p>
               <img src={e.image} alt='Imagen de videogames'/>
               </div>)
            } 
        </div>
    )
}

const mapStateToProps = (store) => {
    return{
        videogames: store.videoGames,
    }
} 

export default connect(mapStateToProps, {searchGames})(Card);
